class SyscallTable:
    """Sytem call table for Arm architectures.
    """
    def __init__(self, bits: PtrlibBitsT):
        self._bits = bits

    @cache
    def __getitem__(self, name: str) -> int:
        if self._bits == 32 and name in _syscall_table_32:
            return _syscall_table_32[name]

        if self._bits == 64 and name in _syscall_table_64:
            return _syscall_table_64[name]

        raise KeyError(f"No such system call: {name}")

    def __getattr__(self, name: str) -> int:
        return self[name]


__all__ = ['SyscallTable']
